import React, { Component } from 'react';
import './App.css';
import { Router } from '@reach/router';
import Homepage from './Components/Homepage';
import NavBar from './Components/NavBar';
import Articles from './Components/Articles';
import Topics from './Components/Topics';
import Users from './Components/Users';
import SingleArticle from './Components/SingleArticle';
import * as api from './Utils/api';

class App extends Component {

  state = {
    sidebarClassName: 'sidebar',
    user: ''
  }

  render() {
    const { sidebarClassName, user } = this.state;

    return (
      <div className="App">
        <div className={sidebarClassName}>
          <div className="logo">SJD News</div>
          <NavBar login={this.login} user={user} toggleSidebar={this.toggleSidebar} />
        </div>
        <Router className="mainDisplay">
          <Homepage path="/" toggleSidebar={this.toggleSidebar} />
          <Topics path="/topics" toggleSidebar={this.toggleSidebar} />
          <Articles path="/topics/:topic/articles" toggleSidebar={this.toggleSidebar} />
          <Articles path="/articles" toggleSidebar={this.toggleSidebar} />
          <SingleArticle path="articles/:article_id" toggleSidebar={this.toggleSidebar} />
          <Users path="/users" toggleSidebar={this.toggleSidebar} />
        </Router>
      </div>
    );
  }

  toggleSidebar = () => {
    let { sidebarClassName } = this.state;
    sidebarClassName = sidebarClassName === 'sidebar' ? 'sidebar active' : 'sidebar'
    this.setState({ sidebarClassName });
  }

  login = (user) => {
    api.getUserByUsername(user)
      .then(user => {
        sessionStorage.setItem('user', JSON.stringify(user))
        this.setState({ user })
      })
      .catch(err => console.log(err))
  }
}

export default App;
