import React, { Component } from 'react';
import Header from './Header';
import '../CSS/Topics.css'
import { navigate } from '@reach/router';
import * as api from '../Utils/api'


class Topics extends Component {

  state = {
    loading: true,
    topics: [],
    page: 1,
    isLastPage: false,
  }

  render() {
    const { toggleSidebar } = this.props
    const { topics, loading, page, isLastPage } = this.state

    if (!loading) {
      return (
        <React.Fragment>
          <Header toggleSidebar={toggleSidebar} heading="Topics" />
          <main>
            <div>
              <button onClick={() => this.updatePageNumber(-1)} disabled={page === 1}>Previous</button>
              <button onClick={() => this.updatePageNumber(1)} disabled={isLastPage}>Next</button>
            </div>
            {topics.map(topic => (
              <div className="card" key={topic.slug} onClick={() => this.handleClick(topic.slug)}>
                <h3>{topic.slug}</h3>
                <p>Description: {topic.description}</p>
              </div>
            ))}
          </main>
        </React.Fragment>
      )
    }
    else return (<p className="loading">is loading...</p>);
  }

  componentDidMount() {
    this.fetchTopics();
  }

  componentDidUpdate(prevProps, prevState) {
    const pageUpdated = prevState.page !== this.state.page;
    if (pageUpdated) {
      this.fetchTopics();
    }
  }

  fetchTopics = () => {
    const { page } = this.state;
    api.getTopics(page)
      .then(topics => this.setState({ topics }))
      .catch(err => console.log(err))
    api.getTopics(page + 1)
      .then(topics => {
        if (!topics.length) this.setState({ isLastPage: true, loading: false })
        else this.setState({ isLastPage: false, loading: false })
      })
      .catch(err => this.setState({ isLastPage: true, loading: false }))
  }

  handleClick = topic => {
    navigate(`/topics/${topic}/articles`);
  }

  updatePageNumber = direction => {
    this.setState(({ page }) => ({
      page: page + direction
    }))
  }

  resetPageNumber = () => {
    this.setState({ page: 1 })
  }
}

export default Topics;
