import React, { Component } from 'react';
import './App.css';
import { Router } from '@reach/router';
import Homepage from './Components/Homepage';
import NavBar from './Components/NavBar';
import Header from './Components/Header';

class App extends Component {

  state = {
    sidebarClassName: 'sidebar',
    showHeader: false
  }

  render() {
    const { sidebarClassName, showHeader } = this.state;

    return (
      <div className="App">
        <div className={sidebarClassName}>
          <div className="logo">SJD News</div>
          <NavBar />
        </div>
        <Header toggleSidebar={this.toggleSidebar} sidebarClassName={sidebarClassName} showHeader={showHeader} />
        <Router className="mainDisplay">
          <Homepage path="/" toggleHeader={this.toggleHeader} />
        </Router>
      </div>
    );
  }

  toggleSidebar = (sidebarClassName) => {
    this.setState({ sidebarClassName });
  }

  toggleHeader = (showHeader) => {
    this.setState({
      showHeader: !this.state.showHeader
    });
  }
}



export default App;
