import React, { Component } from 'react';
import Header from './Header';
import '../CSS/Articles.css'

class Articles extends Component {

  state = {
    loading: true,
    articles: [
      { description: 'Code is love, code is life', slug: 'coding' },
      { description: 'FOOTIE!', slug: 'football' },
      { description: 'Hey good looking, what you got cooking?', slug: 'cooking' },
    ]
  }

  render() {
    const { toggleSidebar } = this.props;
    const { articles, loading } = this.state

    if (!loading) {
      return (
        <React.Fragment>
          <Header toggleSidebar={toggleSidebar} heading="Articles" />
          <main>Articles</main>
        </React.Fragment>
      )
    }
    else return (<p className="loading">is loading...</p>);
  }

  componentDidMount() {
    this.fetchArticles();
  }

  fetchArticles = () => {
    // api.getStudents()
    //   .then(students => this.setState({ students, loading: false }))
    //   .catch(err => console.log(err))
    this.setState({ loading: false })
  }
}

export default Articles;
