import React from 'react';
import { navigate } from '@reach/router';

const ListArticles = ({ artices }) => {
  return (
    <ul>
      {artices.map(artices => (
        <li key={`${artices.slug}`} onClick={() => handleClick(artices.slug)}>
          <h3>{artices.slug}</h3>
          <p>
            Description: {artices.description}
          </p>
        </li>
      ))
      }
    </ul >
  );
};

const handleClick = slug => {
  navigate(`/topics/${slug}/articles`);
}

export default ListArticles;
