import React, { Component } from 'react';
import Header from './Header';
import '../CSS/NewArticle.css';
import * as api from '../Utils/api';

class NewArticle extends Component {
  render() {
    const { toggleSidebar, topic, loggedIn } = this.props;
    const heading = (topic === "undefined") ?
      'Create a New Article'
      : `Create a New ${topic.charAt(0).toUpperCase() + topic.slice(1)} Article`;

    return (
      <React.Fragment>
        <Header toggleSidebar={toggleSidebar} heading={heading} loggedIn={loggedIn} />
        <main className="newArticleMain">
          <div>
            <p>Select a Topic: get the array of topics, map over and put into the list</p>
            <select value="" onChange={this.handleTopicSelect} >
              <option default value="new">New Topic</option>
              <option key="created_at" value="created_at">Date Created</option>
              <option key="comment_count" value="comment_count">Comments</option>
              <option key="votes" value="votes">Votes</option>
            </select >
          </div>
          <form className="newTopicForm"> if New Topic is selected:
            <p>New Topic Name Field</p>
            <p>New Topic Description Field</p>
          </form>
          <form className="newArticleForm">
            <p>Article Title:</p>
            <p>Article:</p>
          </form>
        </main>
      </React.Fragment>
    );
  }

  fetchTopics = () => {
    api.getTopics()
      .then(topics => this.setState({ topics, loading: false }))
      .catch(err => console.log(err))
  }
}

export default NewArticle;
