import React, { Component } from 'react';
import Header from './Header';
import '../CSS/Articles.css'
import { navigate } from '@reach/router';
import * as api from '../Utils/api'

class SingleArticle extends Component {
  state = {
    loading: true,
    article: {},
    article_id: ''
  }

  render() {
    const { toggleSidebar } = this.props;
    const { article, loading } = this.state

    if (!loading) {
      return (
        <React.Fragment>
          <Header toggleSidebar={toggleSidebar} heading={article.title} />
          <main>
            <div className="articleCard">
              <img></img>
              <div className="articleInfo">
                <p>Topic</p>
                <p>Date</p>
                <p>User</p>
                <p>Comments</p>
              </div>
              <div className="article">
                <h3>Title</h3>
                <p>article body</p>
              </div>
            </div>
          </main>
        </React.Fragment>
      )
    }
    else return (<p className="loading">is loading...</p>);
  }

  componentDidMount() {
    this.fetchArticleById();
  }

  componentDidUpdate(prevProps, prevState) {
    const articleUpdated = prevProps.article_id !== this.props.article_id;

    if (articleUpdated) {
      this.fetchArticleById();
    }
  }

  fetchArticleById = () => {
    const { article_id } = this.props;
    api.getArticleById(article_id)
      .then(articles => {
        this.setState({ articles, loading: false, article_id })
      })
      .catch(err => console.log(err))
  }
}

export default SingleArticle;
